function t(n){const e=document.getElementById("dropdownMenu"),o=document.querySelector(".nav-toggle");!e.contains(n.target)&&!o.contains(n.target)&&(e.classList.remove("active"),document.removeEventListener("click",t))}document.addEventListener("DOMContentLoaded",function(){document.querySelectorAll(".dropdown-item").forEach(e=>{e.addEventListener("click",function(){document.getElementById("dropdownMenu").classList.remove("active"),document.removeEventListener("click",t)})})});document.addEventListener("keydown",function(n){if(n.key==="Escape"){const e=document.getElementById("dropdownMenu");e.classList.contains("active")&&(e.classList.remove("active"),document.removeEventListener("click",t))}});
